CREATE SCHEMA RACUNALA;
CREATE TABLE RACUNALA.KOMPONENTA( ID INT NOT NULL GENERATED ALWAYS AS IDENTITY, NAZIV_PROIZVODJACA VARCHAR(50) NOT NULL, CIJENA DECIMAL(8,2) NOT NULL, PRIMARY KEY (ID) );
CREATE TABLE RACUNALA.MATICNA_PLOCA( ID INT NOT NULL, TIP VARCHAR(50) NOT NULL, TIP_SUCELJA_ZA_PROCESOR VARCHAR(50) NOT NULL, MAX_MEMORIJSKIH_MODULA INT NOT NULL, PRIMARY KEY (ID), FOREIGN KEY (ID) REFERENCES RACUNALA.KOMPONENTA(ID) );
CREATE TABLE RACUNALA.PROCESOR( ID INT NOT NULL, TIP VARCHAR(50) NOT NULL,TIP_SUCELJA VARCHAR(50) NOT NULL, BRZINA DECIMAL(4,2) NOT NULL, PRIMARY KEY (ID), FOREIGN KEY (ID) REFERENCES RACUNALA.KOMPONENTA(ID) );
CREATE TABLE RACUNALA.RADNA_MEMORIJA( ID INT NOT NULL, TIP VARCHAR(50) NOT NULL, KAPACITET INT NOT NULL, PRIMARY KEY (ID), FOREIGN KEY (ID) REFERENCES RACUNALA.KOMPONENTA(ID) );
CREATE TABLE RACUNALA.TVRDI_DISK( ID INT NOT NULL, TIP VARCHAR(50) NOT NULL, KAPACITET DECIMAL(6,2) NOT NULL, PRIMARY KEY (ID), FOREIGN KEY (ID) REFERENCES RACUNALA.KOMPONENTA(ID) );
CREATE TABLE RACUNALA.RACUNALO( ID INT NOT NULL GENERATED ALWAYS AS IDENTITY, MATICNA_PLOCA_ID INT NOT NULL, PROCESOR_ID INT NOT NULL, RADNA_MEMORIJA_ID INT NOT NULL, BROJ_MODULA_RADNE_MEMORIJE INT NOT NULL, TVRDI_DISK_ID INT NOT NULL, PRIMARY KEY (ID), FOREIGN KEY (MATICNA_PLOCA_ID) REFERENCES RACUNALA.MATICNA_PLOCA(ID), FOREIGN KEY (PROCESOR_ID) REFERENCES RACUNALA.PROCESOR(ID), FOREIGN KEY (RADNA_MEMORIJA_ID) REFERENCES RACUNALA.RADNA_MEMORIJA(ID), FOREIGN KEY (TVRDI_DISK_ID) REFERENCES RACUNALA.TVRDI_DISK(ID) );